import React, { useState, useEffect } from 'react';
import { useNavigate, useParams, Link } from 'react-router-dom';
import { FaStar } from 'react-icons/fa';
import styles from './ProfileView.module.css';

export default function ProfileView() {
  const navigate = useNavigate();
  const { userId: paramUserId } = useParams();

  const [userId, setUserId] = useState(paramUserId);
  const [currentUserId, setCurrentUserId] = useState(null); // ID Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½Ð½Ð¾Ð³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  const [userEmail, setUserEmail] = useState('');
  const [activeTab, setActiveTab] = useState('projects');
  const [formData, setFormData] = useState({
    photo: '',
    firstName: '',
    lastName: '',
    username: '',
    about: '',
  });
  const [currentUserData, setCurrentUserData] = useState({ // Ð”Ð°Ð½Ð½Ñ‹Ðµ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½Ð½Ð¾Ð³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
    firstName: '',
    lastName: '',
    photo: '',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [projectsAsCustomer, setProjectsAsCustomer] = useState([]);
  const [completedExecutorProjects, setCompletedExecutorProjects] = useState([]);
  const [inProcessExecutorCases, setInProcessExecutorCases] = useState([]);

  const [reviews, setReviews] = useState([]);
  const [newReviewText, setNewReviewText] = useState('');
  const [newReviewRating, setNewReviewRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);

  // Ð”Ð•Ð‘ÐÐ“: Ð»Ð¾Ð³Ð¸Ñ€ÑƒÐµÐ¼ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÑƒ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
  console.log('=== ProfileView Ð—ÐÐ“Ð Ð£Ð–Ð•Ð ===');
  console.log('paramUserId:', paramUserId);
  console.log('userId:', userId);
  console.log('currentUserId Ð¸Ð· localStorage:', localStorage.getItem('currentUserId'));

  // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸ÑŽ Ð¿Ñ€Ð¸ Ð·Ð°Ð³Ñ€ÑƒÐ·ÐºÐµ ÐºÐ¾Ð¼Ð¿Ð¾Ð½ÐµÐ½Ñ‚Ð°
  useEffect(() => {
    console.log('=== useEffect Ð¿Ñ€Ð¾Ð²ÐµÑ€ÐºÐ¸ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ð¸ ===');
    const checkAuth = () => {
      const storedUserId = localStorage.getItem('currentUserId');
      console.log('storedUserId:', storedUserId);
      if (!storedUserId) {
        console.log('âŒ ÐÐµÑ‚ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð°Ñ†Ð¸Ð¸ - Ñ€ÐµÐ´Ð¸Ñ€ÐµÐºÑ‚ Ð½Ð° /signin');
        navigate('/signin');
        return;
      }
      setCurrentUserId(storedUserId);
      console.log('âœ… currentUserId ÑƒÑÑ‚Ð°Ð½Ð¾Ð²Ð»ÐµÐ½:', storedUserId);
    };

    checkAuth();
  }, [navigate]);

  // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ñ‚ÐµÐºÑƒÑ‰ÐµÐ³Ð¾ Ð°Ð²Ñ‚Ð¾Ñ€Ð¸Ð·Ð¾Ð²Ð°Ð½Ð½Ð¾Ð³Ð¾ Ð¿Ð¾Ð»ÑŒÐ·Ð¾Ð²Ð°Ñ‚ÐµÐ»Ñ
  useEffect(() => {
