import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { FaStar } from 'react-icons/fa';
import styles from './ProfilePage.module.css';

export default function ProfileView() {
  const { userId } = useParams();
  const [profile, setProfile] = useState(null);
  const [projects, setProjects] = useState([]);
  const [cases, setCases] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchProfileData() {
      try {
        setLoading(true);
        const [profileRes, projectsRes, casesRes, reviewsRes] = await Promise.all([
          fetch(`http://localhost:3001/profile/${userId}`),
          fetch(`http://localhost:3001/projects?userId=${userId}`),
          fetch(`http://localhost:3001/cases?userId=${userId}`),
          fetch(`http://localhost:3001/reviews?userId=${userId}`),
        ]);

        if (!profileRes.ok) throw new Error('Ошибка загрузки профиля');
        if (!projectsRes.ok) throw new Error('Ошибка загрузки проектов');
        if (!casesRes.ok) throw new Error('Ошибка загрузки кейсов');
        if (!reviewsRes.ok) throw new Error('Ошибка загрузки отзывов');

        const profileData = await profileRes.json();
        const projectsData = await projectsRes.json();
        const casesData = await casesRes.json();
        const reviewsData = await reviewsRes.json();

        setProfile(profileData);
        setProjects(projectsData);
        setCases(casesData);
        setReviews(reviewsData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchProfileData();
  }, [userId]);

  const renderStars = (rating) => {
    return [...Array(5)].map((_, idx) => (
      <FaStar key={idx} color={idx < rating ? '#ffbe5a' : '#ccc'} />
    ));
  };

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;
  if (!profile) return <p>Профиль не найден</p>;

  return (
    <div className={styles.container}>
      <div className={styles.profileHeader}>
        <img src={`http://localhost:3001${profile.photo}`} alt="User" className={styles.photo} />
        <h1>{profile.firstName} {profile.lastName}</h1>
        <p>{profile.email}</p>
        <p>{profile.description || 'Информация отсутствует'}</p>
      </div>

      <div className={styles.tabs}>
        <h2>Проекты</h2>
        {projects.length === 0 ? <p>Нет проектов</p> : (
          projects.map(proj => (
            <Link to={`/projects/${proj.id}`} key={proj.id} className={styles.projectLink}>
              <div>{proj.title}</div>
            </Link>
          ))
        )}

        <h2>Кейсы</h2>
        {cases.length === 0 ? <p>Нет кейсов</p> : (
          cases.map(c => (
            <Link to={`/cases/${c.id}`} key={c.id} className={styles.caseLink}>
              <div>{c.title}</div>
            </Link>
          ))
        )}

        <h2>Отзывы</h2>
        {reviews.length === 0 ? <p>Нет отзывов</p> : (
          reviews.map(r => (
            <div key={r.id} className={styles.review}>
              <div><b>{r.reviewerName}</b> {renderStars(r.rating)}</div>
              <p>{r.text}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
