import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaStar } from 'react-icons/fa';
import styles from './ProfilePage.module.css';
import { useAuth } from '../context/AuthContext';
import { doc, getDoc, setDoc } from 'firebase/firestore';
import { db } from '../firebase';

export default function ProfilePage() {
  const { user, signOut } = useAuth();
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState('projects');
  const [formData, setFormData] = useState({
    photo: '',
    firstName: '',
    lastName: '',
    username: '',
    about: '',
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (!user) return;

    const fetchUserData = async () => {
      const docRef = doc(db, "users", user.uid);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        setFormData(docSnap.data());
      } else {
        setFormData({ username: user.email });
      }
      setLoading(false);
    };

    fetchUserData();
  }, [user]);

  const handleLogout = async () => {
    await signOut();
    navigate('/');
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if(file){
      const reader = new FileReader();
      reader.onload = () => setFormData(prev => ({ ...prev, photo: reader.result }));
      reader.readAsDataURL(file);
    }
  };

  const toggleEdit = () => setIsEditing(!isEditing);

  const handleSave = async (e) => {
    e.preventDefault();
    if(!user) return;

    try{
      const docRef = doc(db, "users", user.uid);
      await setDoc(docRef, formData);
      alert('Данные сохранены');
      setIsEditing(false);
    } catch(error){
      alert('Ошибка сохранения: ' + error.message);
    }
  };

  // Заглушки для проектов, кейсов и отзывов, оставьте ваши изначальные
  const [projects] = useState([]);
  const [cases] = useState([]);
  const [reviews] = useState([]);

  if(!user) return <p>Загрузка...</p>;
  if(loading) return <p>Загрузка данных пользователя...</p>;

  return (
    <>
      <header className={styles.header}>
        <Link to="/"><img src="images/logosmall.svg" alt="IdeaFlow logo" style={{height:80}} /></Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile"><button className={styles.buttonYellow}>Разместить проект</button></Link>
          <Link to="/cases"><button className={styles.buttonYellow}>Приступить к проекту</button></Link>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <div className={styles.userInfo}>
          <div className={styles.photoWrapper} title="Нажмите, чтобы изменить фото">
            {formData.photo ? (
              <img src={formData.photo} alt="User" className={styles.userPhoto} />
            ) : (
              <div className={styles.userPhotoPlaceholder}>Фото</div>
            )}
            {isEditing && (
              <input type="file" accept="image/*" onChange={handlePhotoChange} className={styles.fileInput} title="Выберите фото" />
            )}
          </div>

          {isEditing ? (
            <form className={styles.editForm} onSubmit={handleSave}>
              <input type="text" name="firstName" value={formData.firstName} onChange={handleInputChange} placeholder="Имя" className={styles.editInput} />
              <input type="text" name="lastName" value={formData.lastName} onChange={handleInputChange} placeholder="Фамилия" className={styles.editInput} />
              <input type="text" name="username" value={formData.username} readOnly placeholder="Юзернейм" className={styles.editInput} title="Юзернейм заполняется автоматически" />
              <textarea name="about" value={formData.about} onChange={handleInputChange} placeholder="О себе" className={styles.editTextarea} />
              <button type="submit" className={styles.saveButton}>Сохранить</button>
              <button type="button" className={styles.cancelButton} onClick={toggleEdit}>Отмена</button>
            </form>
          ) : (
            <div className={styles.infoDisplay}>
              <h2>{formData.firstName} {formData.lastName}</h2>
              <p>{formData.username}</p>
              <p><b>О себе:</b> {formData.about || 'Нет информации'}</p>
              <button className={styles.editButton} onClick={toggleEdit}>Изменить информацию</button>
            </div>
          )}
        </div>

        <div className={styles.actionButtons}>
          <button className={styles.actionButton1} onClick={() => navigate('/projects/new')}>Разместить проект</button>
          <button className={styles.actionButton2} onClick={() => navigate('/cases')}>Приступить к проекту</button>
          <button className={styles.logoutButton} onClick={handleLogout}>Выйти из профиля</button>
        </div>
      </div>

      <div className={styles.centerWrapper}>
        <div className={styles.userInfo}>
          <div className={styles.photoWrapper} title="Нажмите, чтобы изменить фото">
            {formData.photo ? (
              <img src={formData.photo} alt="User" className={styles.userPhoto} />
            ) : (
              <div className={styles.userPhotoPlaceholder}>Фото</div>
            )}
            {isEditing && (
              <input
                type="file"
                accept="image/*"
                onChange={handlePhotoChange}
                className={styles.fileInput}
                title="Выберите фото"
              />
            )}
          </div>

          {isEditing ? (
            <form className={styles.editForm} onSubmit={handleSave}>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                placeholder="Имя"
                className={styles.editInput}
              />
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                placeholder="Фамилия"
                className={styles.editInput}
              />
              <input
                type="text"
                name="username"
                value={formData.username}
                readOnly
                placeholder="Юзернейм"
                className={styles.editInput}
                title="Юзернейм соответствует вашему email"
              />
              <textarea
                name="about"
                value={formData.about}
                onChange={handleInputChange}
                placeholder="Информация о себе"
                className={styles.editTextarea}
              />
              <button type="submit" className={styles.saveButton}>Сохранить</button>
              <button type="button" className={styles.cancelButton} onClick={toggleEdit}>Отмена</button>
            </form>
          ) : (
            <div className={styles.infoDisplay}>
              <h2>{formData.firstName} {formData.lastName}</h2>
              <p>{formData.username}</p>
              <p><b>О себе:</b> {formData.about || 'Нет информации'}</p>
              <button className={styles.editButton} onClick={toggleEdit}>Изменить информацию</button>
            </div>
          )}
        </div>

        <div className={styles.actionButtons}>
          <button className={styles.actionButton1} onClick={() => navigate('/projects/new')}>
            Разместить проект
          </button>
          <button className={styles.actionButton2} onClick={() => navigate('/cases')}>
            Приступить к проекту
          </button>
          <button className={styles.logoutButton} onClick={handleLogout}>
            Выйти из профиля
          </button>
        </div>
      </div>

      <div className={styles.tabsContainer}>
        <div className={styles.tabs}>
          <button
            className={`${styles.tabButton} ${activeTab === 'projects' ? styles.active : ''}`}
            onClick={() => setActiveTab('projects')}
          >
            Проекты
          </button>
          <button
            className={`${styles.tabButton} ${activeTab === 'cases' ? styles.active : ''}`}
            onClick={() => setActiveTab('cases')}
          >
            Кейсы
          </button>
          <button
            className={`${styles.tabButton} ${activeTab === 'reviews' ? styles.active : ''}`}
            onClick={() => setActiveTab('reviews')}
          >
            Отзывы
          </button>
        </div>
        {renderTabContent()}
      </div>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a><br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer"><img src="images/facebook.svg" alt="Facebook" /></a>
            <a href="https://twitter.com" target="_blank" rel="noopener noreferrer"><img src="images/twitterx.svg" alt="Twitter" /></a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer"><img src="images/instagram.svg" alt="Instagram" /></a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
