import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import styles from './RegisterPage.module.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleRegister = async (e) => {
    e.preventDefault();
    if (isSubmitting) return; // блокируем множественные сабмиты
    setIsSubmitting(true);
    setError('');

    if (!email || !password) {
      setError('Пожалуйста, заполните email и пароль');
      setIsSubmitting(false);
      return;
    }

    if (password.length < 6) {
      setError('Пароль должен содержать минимум 6 символов');
      setIsSubmitting(false);
      return;
    }

    try {
      await register(email.trim(), password);
      navigate('/profile');
    } catch (err) {
      let message = '';
      if (err.code === 'auth/email-already-in-use') {
        message = 'Этот email уже зарегистрирован.';
      } else if (err.code === 'auth/invalid-email') {
        message = 'Неверный формат email.';
      } else if (err.code === 'auth/weak-password') {
        message = 'Пароль слишком слабый (минимум 6 символов).';
      } else {
        message = 'Ошибка регистрации: ' + (err.message || 'неизвестная ошибка');
      }
      setError(message);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <header className={styles.header}> 
        {/* ваш header */}
      </header>

      <div className={styles.centerWrapper}>
        <section className={styles.signInBox}>
          <h2 className={styles.signInTitle}>Регистрация</h2>
          <form onSubmit={handleRegister} noValidate>
            <input
              className={styles.inputField}
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              autoComplete="email"
              disabled={isSubmitting}
            />
            <input
              className={styles.inputField}
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              minLength={6}
              autoComplete="new-password"
              disabled={isSubmitting}
            />
            <button type="submit" className={styles.submitBtn} disabled={isSubmitting}>
              {isSubmitting ? 'Регистрация...' : 'Зарегистрироваться'}
            </button>
          </form>
          {error && <p style={{ color: 'red', marginTop: 10 }}>{error}</p>}
          <Link to="/signin" className={styles.registerLink}>Уже есть аккаунт? Войти</Link>
        </section>
      </div>

      <footer className={styles.footer}>
        {/* ваш footer */}
      </footer>
    </>
  );
}
