export default function ProfileView() {
  const { userId } = useParams();
  const [formData, setFormData] = useState({
    photo: '',
    firstName: '',
    lastName: '',
    username: '',
    about: '',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!userId) return;

    async function fetchData() {
      try {
        setLoading(true);
        const res = await fetch(`http://localhost:3001/profile/${userId}`);
        if (!res.ok) throw new Error(`Ошибка: ${res.status}`);
        const data = await res.json();
        setFormData({
          photo: data.photo || '',
          firstName: data.firstName || '',
          lastName: data.lastName || '',
          username: data.email || '',
          about: data.description || '',
        });
      } catch (err) {
        setError(err.message || 'Ошибка загрузки данных');
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [userId]);

  if (loading) return <p>Загрузка...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div className={styles.profileView}>
      <div className={styles.profileHeader}>
        {formData.photo ? (
          <img src={`http://localhost:3001${formData.photo}`} alt="User" className={styles.userPhoto} />
        ) : (
          <div className={styles.userPhotoPlaceholder}>Фото</div>
        )}
        <h1>{formData.firstName} {formData.lastName}</h1>
        <p>{formData.username}</p>
        <p>{formData.about || 'Нет информации'}</p>
      </div>
    </div>
  );
}
