import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaStar } from 'react-icons/fa'; // импорт иконок звезд
import styles from './ProfilePage.module.css';
import { useAuth } from '../context/AuthContext';

export default function ProfilePage() {
  const { user, signOut } = useAuth();
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState('projects');
  const [formData, setFormData] = useState({
    photo: user?.photo || '',
    firstName: user?.firstName || '',
    lastName: user?.lastName || '',
    username: user?.username || '',
    about: user?.about || '',
  });

  const [projects] = useState([
    { id: 1, performer: 'Алина Конопелько', topic: 'Разработка логотипа', photo: '/images/photo1.jpg' },
    { id: 2, performer: 'Полина Рюмина', topic: 'Верстка сайта', photo: '/images/photo2.jpg' },
    { id: 3, performer: 'Олег Чистяков', topic: 'Брендинг', photo: '/images/photo5.jpg' },
    { id: 4, performer: 'Дмитрий Шмагин', topic: 'Программа C#', photo: '/images/photo6.jpg' },
  ]);

  const [cases] = useState([
    { id: 1, performer: 'Иван Иванов', topic: 'Дизайн мобильного приложения', photo: '/images/photo2.jpg' },
    { id: 2, performer: 'Мария Смирнова', topic: 'Разработка корпоративного сайта', photo: '/images/photo1.jpg' },
    { id: 3, performer: 'Алексей Петров', topic: 'SEO оптимизация', photo: '/images/photo5.jpg' },
  ]);

  const [reviews, setReviews] = useState([
    { id: 1, username: 'ivanov', name: 'Иван Иванов', photo: '/images/photo2.jpg', text: 'Отличная работа!', rating: 5 },
    { id: 2, username: 'petrov', name: 'Пётр Петров', photo: '/images/photo1.jpg', text: 'Очень доволен!', rating: 4 },
  ]);
  const [newReviewText, setNewReviewText] = useState('');
  const [newReviewRating, setNewReviewRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);

  if (!user) {
    return <p>Загрузка профиля...</p>;
  }

  const handleLogout = async () => {
    try {
      await signOut(); // ждём завершения выхода
      navigate('/');
    } catch (error) {
      console.error('Ошибка выхода:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setFormData(prev => ({ ...prev, photo: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const toggleEdit = () => setIsEditing(!isEditing);

  const handleSave = () => {
    setIsEditing(false);
  };

  const handleAddReview = () => {
    if (newReviewText.trim() === '' || newReviewRating === 0) return;

    const newReview = {
      id: reviews.length + 1,
      username: user.username || 'anonymous',
      name: `${formData.firstName} ${formData.lastName}`.trim() || 'Anonymous',
      photo: formData.photo || '',
      text: newReviewText.trim(),
      rating: newReviewRating,
    };

    setReviews([...reviews, newReview]);
    setNewReviewText('');
    setNewReviewRating(0);
    setHoverRating(0);
  };

  const renderStars = (rating) => (
    <>
      {[...Array(5)].map((_, index) => {
        const starValue = index + 1;
        return (
          <FaStar
            key={index}
            size={18}
            color={starValue <= rating ? '#ffbe5a' : '#ccc'}
          />
        );
      })}
    </>
  );

  const averageRating = reviews.length > 0
    ? (reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length).toFixed(1)
    : 0;

  const renderTabContent = () => {
    switch (activeTab) {
      case 'projects':
        return (
          <div className={`${styles.tabContent} ${styles.projectsTab}`}>
            <h3>Проекты пользователя</h3>
            <div className={styles.projectsGrid}>
              {projects.map(p => (
                <div key={p.id} className={styles.projectCard}>
                  <img src={p.photo} alt={`Фото исполнителя ${p.performer}`} className={styles.projectImage} />
                  <div className={styles.projectInfo}>
                    <div className={styles.projectPerformer}>{p.performer}</div>
                    <div className={styles.projectTopic}>{p.topic}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case 'cases':
        return (
          <div className={`${styles.tabContent} ${styles.casesTab}`}>
            <h3>Кейсы пользователя</h3>
            <div className={styles.casesGrid}>
              {cases.map(c => (
                <div key={c.id} className={styles.caseCard}>
                  <img src={c.photo} alt={`Фото исполнителя ${c.performer}`} className={styles.caseImage} />
                  <div className={styles.caseInfo}>
                    <div className={styles.casePerformer}>{c.performer}</div>
                    <div className={styles.caseTopic}>{c.topic}</div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      case 'reviews':
        return (
          <div className={styles.reviewContainer}>
            <h3>
              Отзывы пользователя{' '}
              <span style={{ fontFamily: 'Arial', fontWeight: 'normal', fontSize: '1rem', marginLeft: '10px' }}>
                ({averageRating} ★)
              </span>
            </h3>
            <div className={styles.reviewListCustom}>
              {reviews.map(r => (
                <div key={r.id} className={styles.reviewItemCustom}>
                  <div className={styles.reviewPhotoCustom}>
                    {r.photo ? <img src={r.photo} alt={r.name} /> : <div className={styles.userPhotoPlaceholderCustom}></div>}
                  </div>
                  <div>
                    <b>{r.name}</b>
                    <p>{r.text}</p>
                    <div>{renderStars(r.rating)}</div>
                  </div>
                </div>
              ))}
            </div>
            <div className={styles.reviewFormCustom}>
              <textarea
                placeholder="Оставьте отзыв..."
                value={newReviewText}
                onChange={e => setNewReviewText(e.target.value)}
              />
              <div className={styles.ratingStars}>
                {[...Array(5)].map((_, index) => {
                  const starValue = index + 1;
                  return (
                    <FaStar
                      key={index}
                      size={24}
                      style={{ cursor: 'pointer' }}
                      color={starValue <= (hoverRating || newReviewRating) ? '#ffbe5a' : '#ccc'}
                      onClick={() => setNewReviewRating(starValue)}
                      onMouseEnter={() => setHoverRating(starValue)}
                      onMouseLeave={() => setHoverRating(0)}
                    />
                  );
                })}
              </div>
              <button onClick={handleAddReview}>Добавить отзыв</button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <div className={styles.userInfo}>
          <div className={styles.photoWrapper} title="Нажмите, чтобы изменить фото">
            {formData.photo ? (
              <img src={formData.photo} alt="User" className={styles.userPhoto} />
            ) : (
              <div className={styles.userPhotoPlaceholder}>Фото</div>
            )}
            {isEditing && (
              <input
                type="file"
                accept="image/*"
                onChange={handlePhotoChange}
                className={styles.fileInput}
                title="Выберите фото"
              />
            )}
          </div>

          {isEditing ? (
            <div className={styles.editForm}>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                placeholder="Имя"
                className={styles.editInput}
              />
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                placeholder="Фамилия"
                className={styles.editInput}
              />
              <input
                type="text"
                name="username"
                value={formData.username}
                onChange={handleInputChange}
                placeholder="Юзернейм"
                className={styles.editInput}
              />
              <textarea
                name="about"
                value={formData.about}
                onChange={handleInputChange}
                placeholder="Информация о себе"
                className={styles.editTextarea}
              />
              <button className={styles.saveButton} onClick={handleSave}>Сохранить</button>
              <button className={styles.cancelButton} onClick={toggleEdit}>Отмена</button>
            </div>
          ) : (
            <div className={styles.infoDisplay}>
              <h2>{formData.firstName} {formData.lastName}</h2>
              <p>{formData.username}</p>
              <p><b>О себе:</b> {formData.about || 'Нет информации'}</p>
              <button className={styles.editButton} onClick={toggleEdit}>Изменить информацию</button>
            </div>
          )}
        </div>

        <div className={styles.actionButtons}>
          <button className={styles.actionButton1} onClick={() => navigate('/projects/new')}>
            Разместить проект
          </button>
          <button className={styles.actionButton2} onClick={() => navigate('/cases')}>
            Приступить к проекту
          </button>
          <button className={styles.logoutButton} onClick={handleLogout}>
            Выйти из профиля
          </button>
        </div>
      </div>

      <div className={styles.tabsContainer}>
        <div className={styles.tabs}>
          <button
            className={`${styles.tabButton} ${activeTab === 'projects' ? styles.active : ''}`}
            onClick={() => setActiveTab('projects')}
          >
            Проекты
          </button>
          <button
            className={`${styles.tabButton} ${activeTab === 'cases' ? styles.active : ''}`}
            onClick={() => setActiveTab('cases')}
          >
            Кейсы
          </button>
          <button
            className={`${styles.tabButton} ${activeTab === 'reviews' ? styles.active : ''}`}
            onClick={() => setActiveTab('reviews')}
          >
            Отзывы
          </button>
        </div>
        {renderTabContent()}
      </div>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a>
            <br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#"><img src="images/facebook.svg" alt="Facebook" /></a>
            <a href="#"><img src="images/twitterx.svg" alt="Twitter" /></a>
            <a href="#"><img src="images/instagram.svg" alt="Instagram" /></a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
