import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { FaStar } from 'react-icons/fa'; 
import styles from './ProfilePage.module.css';
import { useAuth } from '../context/AuthContext';
import { doc, getDoc, setDoc } from "firebase/firestore";
import { db } from '../firebase';

export default function ProfilePage() {
  const { user, signOut } = useAuth();
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState('projects');
  const [formData, setFormData] = useState({
    photo: '',
    firstName: '',
    lastName: '',
    username: '',
    about: '',
  });
  const [loading, setLoading] = useState(true);

  // Загрузка данных пользователя из Firestore после загрузки пользователя из контекста
  useEffect(() => {
    if (!user) return;

    const fetchUserData = async () => {
      const docRef = doc(db, "users", user.uid);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        setFormData(docSnap.data());
      } else {
        // Документ не существует — инициализируем username email из user
        setFormData(prev => ({
          ...prev,
          username: user.email,
        }));
      }
      setLoading(false);
    };

    fetchUserData();
  }, [user]);

  const handleLogout = async () => {
    try {
      await signOut();
      navigate('/');
    } catch (error) {
      console.error('Ошибка выхода:', error);
    }
  };


  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };


  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setFormData(prev => ({ ...prev, photo: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };


  const toggleEdit = () => setIsEditing(!isEditing);


  const handleSave = async (e) => {
    e.preventDefault();
    if (!user) return;

    try {
      const docRef = doc(db, "users", user.uid);
      await setDoc(docRef, formData);
      alert("Данные сохранены");
      setIsEditing(false);
    } catch (error) {
      alert("Ошибка при сохранении: " + error.message);
    }
  };

  // Остальной ваш код с projects, cases, reviews и рендером ...

  if (!user) {
    return <p>Загрузка профиля...</p>;
  }

  if (loading) {
    return <p>Загрузка данных пользователя...</p>;
  }

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <div className={styles.userInfo}>
          <div className={styles.photoWrapper} title="Нажмите, чтобы изменить фото">
            {formData.photo ? (
              <img src={formData.photo} alt="User" className={styles.userPhoto} />
            ) : (
              <div className={styles.userPhotoPlaceholder}>Фото</div>
            )}
            {isEditing && (
              <input
                type="file"
                accept="image/*"
                onChange={handlePhotoChange}
                className={styles.fileInput}
                title="Выберите фото"
              />
            )}
          </div>

          {isEditing ? (
            <form className={styles.editForm} onSubmit={handleSave}>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                placeholder="Имя"
                className={styles.editInput}
              />
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                placeholder="Фамилия"
                className={styles.editInput}
              />
              <input
                type="text"
                name="username"
                value={formData.username}
                readOnly
                placeholder="Юзернейм"
                className={styles.editInput}
                title="Юзернейм соответствует вашему email"
              />
              <textarea
                name="about"
                value={formData.about}
                onChange={handleInputChange}
                placeholder="Информация о себе"
                className={styles.editTextarea}
              />
              <button type="submit" className={styles.saveButton}>Сохранить</button>
              <button type="button" className={styles.cancelButton} onClick={toggleEdit}>Отмена</button>
            </form>
          ) : (
            <div className={styles.infoDisplay}>
              <h2>{formData.firstName} {formData.lastName}</h2>
              <p>{formData.username}</p>
              <p><b>О себе:</b> {formData.about || 'Нет информации'}</p>
              <button className={styles.editButton} onClick={toggleEdit}>Изменить информацию</button>
            </div>
          )}
        </div>

        {/* Ваши кнопки и вкладки */}
        {/* ... остальной код ... */}

        <div className={styles.actionButtons}>
          <button className={styles.actionButton1} onClick={() => navigate('/projects/new')}>
            Разместить проект
          </button>
          <button className={styles.actionButton2} onClick={() => navigate('/cases')}>
            Приступить к проекту
          </button>
          <button className={styles.logoutButton} onClick={handleLogout}>
            Выйти из профиля
          </button>
        </div>
      </div>

      {/* Остальной ваш код с табами и футером */}
      {/* ... */}
    </>
  );
}
