import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import styles from './SignInPage.module.css';
import { fetchSignInMethodsForEmail, signInWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase'; // путь к вашему firebase.js

export default function SignInPage() {
  const [email, setEmail] = useState(''); // используем email
  const [password, setPassword] = useState('');
  const { user } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Проверяем, зарегистрирован ли этот email
      const methods = await fetchSignInMethodsForEmail(auth, email);

      if (methods.length === 0) {
        setError('Пользователь с таким email не найден. Пожалуйста, зарегистрируйтесь.');
        return;
      }

      // Если email зарегистрирован, пытаемся войти
      await signInWithEmailAndPassword(auth, email, password);
      navigate('/profile');

    } catch (err) {
      setError('Ошибка входа: ' + err.message);
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to={user ? "/profile" : "/signin"}>Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <section className={styles.signInBox}>
          <h2 className={styles.signInTitle}>Вход</h2>
          <form onSubmit={handleSubmit}>
            <input
              className={styles.inputField}
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
            <input
              className={styles.inputField}
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
            <button type="submit" className={styles.submitBtn}>
              Войти
            </button>
          </form>
          {error && <p style={{ color: 'red', marginTop: 10 }}>{error}</p>}
          <Link to="/register" className={styles.registerLink}>
            У меня нет аккаунта
          </Link>
        </section>
      </div>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a>
            <br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#">
              <img src="images/facebook.svg" alt="Facebook" />
            </a>
            <a href="#">
              <img src="images/twitterx.svg" alt="Twitter" />
            </a>
            <a href="#">
              <img src="images/instagram.svg" alt="Instagram" />
            </a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
