import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import styles from './RegisterPage.module.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleRegister = async (e) => {
    e.preventDefault();
    
    if (isSubmitting) return;
    setIsSubmitting(true);
    setError('');

    // Валидация
    if (!email || !password) {
      setError('Пожалуйста, заполните email и пароль');
      setIsSubmitting(false);
      return;
    }

    if (password.length < 6) {
      setError('Пароль должен содержать минимум 6 символов');
      setIsSubmitting(false);
      return;
    }

    try {
      console.log('Пытаемся зарегистрировать:', email);
      await register(email.trim(), password);
      console.log('Регистрация успешна');
      navigate('/profile');
    } catch (err) {
      console.error('Ошибка регистрации:', err);
      
      let message = 'Ошибка регистрации';
      if (err.code === 'auth/email-already-in-use') {
        message = 'Этот email уже зарегистрирован. Пожалуйста, используйте другой email или войдите.';
      } else if (err.code === 'auth/invalid-email') {
        message = 'Неверный формат email.';
      } else if (err.code === 'auth/weak-password') {
        message = 'Пароль слишком слабый (минимум 6 символов).';
      } else if (err.code === 'auth/network-request-failed') {
        message = 'Проблемы с сетью. Проверьте подключение к интернету.';
      } else if (err.code) {
        message = `Ошибка Firebase: ${err.code}`;
      } else {
        message = 'Ошибка регистрации: ' + (err.message || 'неизвестная ошибка');
      }
      setError(message);
    } finally {
      setIsSubmitting(false);
    }
  };

  // Функция для кнопок навигации
  const handleNavButtonClick = (path) => {
    if (!isSubmitting) {
      navigate(path);
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/" onClick={(e) => isSubmitting && e.preventDefault()}>
          <img src="/images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile" onClick={(e) => isSubmitting && e.preventDefault()}>Профиль</Link>
          <Link to="/cases" onClick={(e) => isSubmitting && e.preventDefault()}>Кейсы</Link>
          <Link to="/projects" onClick={(e) => isSubmitting && e.preventDefault()}>Проекты</Link>
          <button 
            className={`${styles.buttonYellow} ${isSubmitting ? styles.disabled : ''}`}
            onClick={() => handleNavButtonClick('/profile')}
            disabled={isSubmitting}
            type="button"
          >
            Разместить проект
          </button>
          <button 
            className={`${styles.buttonYellow} ${isSubmitting ? styles.disabled : ''}`}
            onClick={() => handleNavButtonClick('/cases')}
            disabled={isSubmitting}
            type="button"
          >
            Приступить к проекту
          </button>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <section className={styles.signInBox}>
          <h2 className={styles.signInTitle}>Регистрация</h2>
          <form onSubmit={handleRegister} noValidate>
            <input
              className={styles.inputField}
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              autoComplete="email"
              disabled={isSubmitting}
            />
            <input
              className={styles.inputField}
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              minLength={6}
              autoComplete="new-password"
              disabled={isSubmitting}
            />
            <button 
              type="submit" 
              className={styles.submitBtn} 
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Регистрация...' : 'Зарегистрироваться'}
            </button>
          </form>
          
          {error && (
            <div style={{ 
              color: 'red', 
              marginTop: 10, 
              padding: 10, 
              background: '#ffe6e6',
              borderRadius: 5,
              border: '1px solid red',
              textAlign: 'center'
            }}>
              <strong>Ошибка:</strong> {error}
            </div>
          )}
          
          <Link 
            to="/signin" 
            className={styles.registerLink}
            onClick={(e) => isSubmitting && e.preventDefault()}
          >
            Уже есть аккаунт? Войти
          </Link>
        </section>
      </div>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="/images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a><br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#"><img src="/images/facebook.svg" alt="Facebook" /></a>
            <a href="#"><img src="/images/twitterx.svg" alt="Twitter" /></a>
            <a href="#"><img src="/images/instagram.svg" alt="Instagram" /></a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
