import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styles from './PageFullProject.module.css';

export default function PageFullProject() {
  const { id } = useParams();
  const [project, setProject] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/projects/${id}`)
      .then(res => {
        if (!res.ok) throw new Error('Ошибка загрузки проекта');
        return res.json();
      })
      .then(data => {
        setProject(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Загрузка проекта...</p>;
  if (error) return <p>Ошибка: {error}</p>;
  if (!project) return <p>Проект не найден</p>;

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>{project.title}</h1>
      {project.cover && <img src={`http://localhost:3001${project.cover}`} alt="Обложка" className={styles.cover} />}
      <p><b>Заказчик:</b> {project.userEmail}</p>
      <p><b>Исполнитель:</b> {project.performerEmail || 'Не назначен'}</p>
      <p><b>Тема:</b> {project.theme}</p>
      <p><b>Задача проекта:</b> {project.description}</p>

      <div className={styles.filesSection}>
        <b>Прикрепленные файлы заказчика:</b>
        <div className={styles.filesList}>
          {project.files && project.files.map((file, i) => (
            <a key={i} href={`http://localhost:3001${file}`} className={styles.fileItem} target="_blank" rel="noreferrer">
              {file.split('/').pop()}
            </a>
          ))}
        </div>
      </div>

      {project.performerFiles && project.performerFiles.length > 0 && (
        <div className={styles.filesSection}>
          <b>Прикрепленные файлы исполнителя:</b>
          <div className={styles.filesList}>
            {project.performerFiles.map((file, i) => (
              <a key={i} href={`http://localhost:3001${file}`} className={styles.fileItem} target="_blank" rel="noreferrer">
                {file.split('/').pop()}
              </a>
            ))}
          </div>
        </div>
      )}

      <p>Статус: {project.status}</p>
    </div>
  );
}
