import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import styles from './ProfilePage.module.css';

export default function ProfileView() {
  const { userId } = useParams();
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    async function fetchProfile() {
      try {
        setLoading(true);
        const res = await fetch(`http://localhost:3001/profile/${userId}`);
        if (!res.ok) throw new Error('Ошибка загрузки профиля');
        const data = await res.json();
        setProfile(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }
    fetchProfile();
  }, [userId]);

  if (loading) return <p>Загрузка профиля...</p>;
  if (error) return <p>Ошибка: {error}</p>;

  return (
    <div className={styles.profileContainer}>
      <img src={`http://localhost:3001${profile.photo}`} alt="User" className={styles.profilePhoto} />
      <h1>{profile.firstName} {profile.lastName}</h1>
      <p>{profile.email}</p>
      <p>{profile.description}</p>
      <Link to="/cases">Вернуться к кейсам</Link>
    </div>
  );
}
