import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import styles from './SignInPage.module.css';

export default function SignInPage() {
  const [email, setEmail] = useState(''); // email
  const [password, setPassword] = useState('');
  const { signIn, user } = useAuth(); // функция из контекста
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const success = await signIn(email, password);
      if (success) {
        navigate('/profile');
      } else {
        setError('Неверный email или пароль');
      }
    } catch (e) {
      setError('Ошибка входа: ' + e.message);
    }
  };

  return (
    <>
    <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>
      
      {/* ...хедер и навигация как в вашем коде*/}
      <div className={styles.centerWrapper}>
        <section className={styles.signInBox}>
          <h2 className={styles.signInTitle}>Вход</h2>
          <form onSubmit={handleSubmit}>
            <input
              className={styles.inputField}
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
            <input
              className={styles.inputField}
              type="password"
              placeholder="Password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
            <button type="submit" className={styles.submitBtn}>
              Войти
            </button>
          </form>
          {error && <p style={{ color: 'red', marginTop: 10 }}>{error}</p>}
          <Link to="/register" className={styles.registerLink}>
            У меня нет аккаунта
          </Link>
        </section>
      </div>
      {/* ...футер */}
    </>
  );
}
