import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext'; // Используйте контекст для регистрации
import styles from './RegisterPage.module.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { register } = useAuth(); // берем метод регистрации из контекста
  const navigate = useNavigate();
  const [error, setError] = useState('');

  const handleRegister = async (e) => {
    e.preventDefault(); // предотвращаем перезагрузку страницы
    setError('');
    try {
      await register(email.trim(), password);
      // После регистрации пользователь автоматически залогинен благодаря контексту
      navigate('/profile'); // переходим на страницу профиля
    } catch (err) {
      let message = '';
      switch (err.code) {
        case 'auth/email-already-in-use':
          message = 'Этот email уже зарегистрирован.';
          break;
        case 'auth/invalid-email':
          message = 'Неверный формат email.';
          break;
        case 'auth/weak-password':
          message = 'Пароль слишком слабый (минимум 6 символов).';
          break;
        default:
          message = 'Ошибка регистрации: ' + err.message;
      }
      setError(message);
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <section className={styles.signInBox}>
          <h2 className={styles.signInTitle}>Регистрация</h2>
          <form onSubmit={handleRegister} noValidate>
            <input
              className={styles.inputField}
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
            <input
              className={styles.inputField}
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              minLength={6}
            />
            <button type="submit" className={styles.submitBtn}>
              Зарегистрироваться
            </button>
          </form>
          {error && <p style={{ color: 'red', marginTop: 10 }}>{error}</p>}
          <Link to="/signin" className={styles.registerLink}>
            Уже есть аккаунт? Войти
          </Link>
        </section>
      </div>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a>
            <br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#">
              <img src="images/facebook.svg" alt="Facebook" />
            </a>
            <a href="#">
              <img src="images/twitterx.svg" alt="Twitter" />
            </a>
            <a href="#">
              <img src="images/instagram.svg" alt="Instagram" />
            </a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
