import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { FaStar } from 'react-icons/fa';
import styles from './ProfilePage.module.css';

export default function ProfileView() {
  const { userId } = useParams();
  const [formData, setFormData] = useState({
    photo: '',
    firstName: '',
    lastName: '',
    username: '',
    about: '',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const [projectsAsCustomer, setProjectsAsCustomer] = useState([]);
  const [completedExecutorProjects, setCompletedExecutorProjects] = useState([]);
  const [completedCases, setCompletedCases] = useState([]);
  const [reviews, setReviews] = useState([]);

  const [activeTab, setActiveTab] = useState('projects');

  useEffect(() => {
    if (!userId) return;

    async function fetchData() {
      try {
        setLoading(true);
        const profileRes = await fetch(`http://localhost:3001/profile/${userId}`);
        if (!profileRes.ok) throw new Error(`Ошибка загрузки данных пользователя: ${profileRes.status}`);
        const profileData = await profileRes.json();
        setFormData({
          photo: profileData.photo || '',
          firstName: profileData.firstName || '',
          lastName: profileData.lastName || '',
          username: profileData.email || '',
          about: profileData.description || '',
        });

        const projectsRes = await fetch(`http://localhost:3001/projects?userId=${userId}`);
        if (projectsRes.ok) {
          const projectsDataRaw = await projectsRes.json();
          setProjectsAsCustomer(projectsDataRaw.filter(p => p.status === 'closed'));
        } else {
          setProjectsAsCustomer([]);
        }

        const casesRes = await fetch(`http://localhost:3001/cases?userId=${userId}`);
        if (casesRes.ok) {
          const casesDataRaw = await casesRes.json();
          setCompletedCases(casesDataRaw.filter(c => c.status === 'closed'));
        } else {
          setCompletedCases([]);
        }

        const execProjectsRes = await fetch(`http://localhost:3001/projects?executorId=${userId}`);
        if (execProjectsRes.ok) {
          const execProjectsDataRaw = await execProjectsRes.json();
          setCompletedExecutorProjects(execProjectsDataRaw.filter(p => p.status === 'closed'));
        } else {
          setCompletedExecutorProjects([]);
        }

        const reviewsRes = await fetch(`http://localhost:3001/reviews?userId=${userId}`);
        if (reviewsRes.ok) {
          const reviewsData = await reviewsRes.json();
          setReviews(reviewsData);
        } else {
          setReviews([]);
        }

      } catch (err) {
        setError(err.message || 'Ошибка загрузки данных');
      } finally {
        setLoading(false);
      }
    }

    fetchData();
  }, [userId]);

  const renderStars = rating =>
    [...Array(5)].map((_, idx) => (
      <FaStar key={idx} size={18} color={idx < rating ? '#ffbe5a' : '#ccc'} />
    ));

  const averageRating =
    reviews.length > 0 ? (reviews.reduce((acc, r) => acc + r.rating, 0) / reviews.length).toFixed(1) : 0;

  const renderTabContent = () => {
    switch (activeTab) {
      case 'projects':
        return (
          <div className={styles.tabContent}>
            <h3>Проекты пользователя</h3>
            {projectsAsCustomer.length === 0 ? (
              <p>Нет проектов</p>
            ) : (
              projectsAsCustomer.map(proj => (
                <div key={proj.id} className={styles.projectCard}>
                  <p>Название: {proj.title}</p>
                  <p>Статус: {proj.status}</p>
                </div>
              ))
            )}
          </div>
        );
      case 'cases':
        return (
          <div className={styles.tabContent}>
            <h3>Завершённые кейсы</h3>
            {completedCases.length === 0 ? (
              <p>Нет кейсов</p>
            ) : (
              completedCases.map(c => (
                <div key={c.id} className={styles.caseCard}>
                  <p>Название: {c.title}</p>
                  <p>Тема: {c.theme}</p>
                </div>
              ))
            )}
          </div>
        );
      case 'reviews':
        return (
          <div className={styles.tabContent}>
            <h3>Отзывы пользователя ({averageRating} ★)</h3>
            {reviews.length === 0 ? (
              <p>Нет отзывов</p>
            ) : (
              reviews.map(r => (
                <div key={r.id} className={styles.reviewItem}>
                  <strong>{r.reviewerName}</strong>
                  <p>{r.text}</p>
                  <div>{renderStars(r.rating)}</div>
                </div>
              ))
            )}
          </div>
        );
      default:
        return null;
    }
  };

  if (loading) return <p>Загрузка данных...</p>;
  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  return (
    <div className={styles.profileView}>
      <div className={styles.profileHeader}>
        {formData.photo ? (
          <img src={`http://localhost:3001${formData.photo}`} alt="User" className={styles.userPhoto} />
        ) : (
          <div className={styles.userPhotoPlaceholder}>Фото</div>
        )}
        <h1>{formData.firstName} {formData.lastName}</h1>
        <p>{formData.username}</p>
        <p>{formData.about || 'Нет информации'}</p>
      </div>

      <div className={styles.tabs}>
        <button className={activeTab === 'projects' ? styles.activeTab : ''} onClick={() => setActiveTab('projects')}>
          Проекты
        </button>
        <button className={activeTab === 'cases' ? styles.activeTab : ''} onClick={() => setActiveTab('cases')}>
          Кейсы
        </button>
        <button className={activeTab === 'reviews' ? styles.activeTab : ''} onClick={() => setActiveTab('reviews')}>
          Отзывы
        </button>
      </div>

      {renderTabContent()}
    </div>
  );
}
