import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import styles from './RegisterPage.module.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const { register } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleRegister = async (e) => {
    e.preventDefault();
    
    if (isSubmitting) return;
    setIsSubmitting(true);
    setError('');

    if (!email || !password) {
      setError('Пожалуйста, заполните email и пароль');
      setIsSubmitting(false);
      return;
    }

    if (password.length < 6) {
      setError('Пароль должен содержать минимум 6 символов');
      setIsSubmitting(false);
      return;
    }

    try {
      await register(email.trim(), password);
      navigate('/profile');
    } catch (err) {
      console.error('Полная ошибка:', err);
      console.error('Код ошибки:', err.code);
      console.error('Сообщение ошибки:', err.message);
      
      // Правильная обработка ошибок Firebase
      if (err.code) {
        switch (err.code) {
          case 'auth/email-already-in-use':
            setError('Этот email уже зарегистрирован. Используйте другой email или войдите в систему.');
            break;
          case 'auth/invalid-email':
            setError('Неверный формат email адреса.');
            break;
          case 'auth/weak-password':
            setError('Пароль слишком слабый. Минимум 6 символов.');
            break;
          case 'auth/network-request-failed':
            setError('Проблемы с сетью. Проверьте подключение к интернету.');
            break;
          default:
            setError(`Ошибка регистрации: ${err.code}`);
        }
      } else {
        setError('Произошла неизвестная ошибка. Попробуйте еще раз.');
      }
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="/images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <div className={styles.centerWrapper}>
        <section className={styles.signInBox}>
          <h2 className={styles.signInTitle}>Регистрация</h2>
          <form onSubmit={handleRegister}>
            <input
              className={styles.inputField}
              type="email"
              placeholder="Email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
              disabled={isSubmitting}
            />
            <input
              className={styles.inputField}
              type="password"
              placeholder="Пароль"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
              minLength={6}
              disabled={isSubmitting}
            />
            <button 
              type="submit" 
              className={styles.submitBtn} 
              disabled={isSubmitting}
            >
              {isSubmitting ? 'Регистрация...' : 'Зарегистрироваться'}
            </button>
          </form>
          
          {error && (
            <div style={{ 
              color: 'red', 
              marginTop: 10, 
              padding: 10, 
              background: '#ffe6e6',
              borderRadius: 5,
              border: '1px solid red',
              textAlign: 'center'
            }}>
              <strong>Ошибка:</strong> {error}
            </div>
          )}
          
          <Link to="/signin" className={styles.registerLink}>
            Уже есть аккаунт? Войти
          </Link>
        </section>
      </div>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="/images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a><br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#"><img src="/images/facebook.svg" alt="Facebook" /></a>
            <a href="#"><img src="/images/twitterx.svg" alt="Twitter" /></a>
            <a href="#"><img src="/images/instagram.svg" alt="Instagram" /></a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
