import React, { useState, useEffect } from 'react';
import styles from './ProjectsPage.module.css';
import { Link } from 'react-router-dom';

export default function ProjectsPage() {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterOpen, setFilterOpen] = useState(false);
  const [selectedTopic, setSelectedTopic] = useState('');

  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, []);

  const projects = [
    { id: 1, performer: 'Алина Конопелько', topic: 'Разработка логотипа', photo: '/images/photo1.jpg' },
    { id: 2, performer: 'Полина Рюмина', topic: 'Верстка сайта', photo: '/images/photo2.jpg' },
    { id: 3, performer: 'Олег Чистяков', topic: 'Брендинг', photo: '/images/photo5.jpg' },
    { id: 4, performer: 'Дмитрий Шмагин', topic: 'Программа C#', photo: '/images/photo6.jpg' },
    { id: 5, performer: 'Алина Конопелько', topic: 'Разработка логотипа', photo: '/images/photo1.jpg' },
    { id: 6, performer: 'Полина Рюмина', topic: 'Верстка сайта', photo: '/images/photo2.jpg' },
    { id: 7, performer: 'Олег Чистяков', topic: 'Брендинг', photo: '/images/photo5.jpg' },
    { id: 8, performer: 'Дмитрий Шмагин', topic: 'Программа C#', photo: '/images/photo6.jpg' },
  ];

  const topics = ['Верстка сайта', 'Веб-дизайн', 'Брендинг', 'Разработка логотипа'];

  const filteredProjects = projects.filter(({ performer, topic }) => {
    const lowerSearch = searchTerm.toLowerCase();
    const matchesSearch = performer.toLowerCase().includes(lowerSearch) || topic.toLowerCase().includes(lowerSearch);
    const matchesTopic = selectedTopic ? topic === selectedTopic : true;
    return matchesSearch && matchesTopic;
  });

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <main className={styles.projectsMain} style={{ position: 'relative' }}>
        <h1 className={styles.projectsTitle}>Реализованные идеи</h1>
        <div className={styles.projectsControls}>
          <button className={styles.projectsFilter} onClick={() => setFilterOpen(true)}>
            Фильтр
            <img src="images/filter-icon.svg" alt="Фильтр" />
          </button>
          <div className={styles.projectsSearchWrapper}>
            <input
              className={styles.projectsSearch}
              type="text"
              placeholder="Поиск"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button className={styles.projectsSearchBtn}>
              <img src="images/search-icon.svg" alt="Поиск" />
            </button>
          </div>
        </div>

        {filterOpen && (
          <div className={styles.modalOverlay} onClick={() => setFilterOpen(false)}>
            <div className={styles.filterSidebar} onClick={e => e.stopPropagation()}>
              <h3>Фильтр по теме</h3>
              <ul>
                {topics.map(topic => (
                  <li key={topic}>
                    <label>
                      <input
                        type="checkbox"
                        name="topicFilter"
                        value={topic}
                        checked={selectedTopic === topic}
                        onChange={() => setSelectedTopic(topic)}
                      />
                      {topic}
                    </label>
                  </li>
                ))}
                <li>
                  <button onClick={() => { setSelectedTopic(''); setFilterOpen(false); }}>
                    Сбросить
                  </button>
                </li>
              </ul>
            </div>
          </div>
        )}

        <p className={styles.projectsRecommendation}>Рекомендации для вас</p>
        <div className={styles.projectsGrid}>
          {filteredProjects.map((project, idx) => (
            <div className={styles.projectCard} key={idx}>
              <img
                className={styles.projectImage}
                src={project.photo}
                alt={`Фото исполнителя ${project.performer}`}
              />
              <div className={styles.projectInfo}>
                <div className={styles.projectPerformer}>{project.performer}</div>
                <div className={styles.projectTopic}>{project.topic}</div>
              </div>
            </div>
          ))}
        </div>
      </main>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a><br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#"><img src="images/facebook.svg" alt="Facebook" /> </a>
            <a href="#"><img src="images/twitterx.svg" alt="Twitter" /> </a>
            <a href="#"><img src="images/instagram.svg" alt="Instagram" /> </a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
