import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styles from './ProjectsPage.module.css';

export default function CasePage() {
  const [cases, setCases] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('http://localhost:3001/cases')
      .then(res => res.json())
      .then(data => {
        setCases(data);
        setLoading(false);
      })
      .catch(() => setLoading(false));
  }, []);

  if (loading) return <p>Загрузка кейсов...</p>;

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
        </nav>
      </header>

      <main className={styles.projectsMain}>
        <h1 className={styles.projectsTitle}>Открытые кейсы</h1>
        <div className={styles.projectsGrid}>
          {cases.map(c => (
            <div key={c.id} className={styles.projectCard}>
              <img src={`http://localhost:3001${c.cover}`} alt="Обложка кейса" className={styles.projectImage} />
              <div className={styles.projectInfo}>
                <div><b>Название:</b> {c.title}</div>
                <div><b>Тема:</b> {c.theme}</div>
                <div><b>Заказчик:</b> {c.userEmail}</div>
                <div><b>Статус:</b> {c.status}</div>
                <Link to={`/cases/${c.id}`}>Подробнее</Link>
              </div>
            </div>
          ))}
        </div>
      </main>
    </>
  );
}
