import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import styles from './PageFullCase.module.css';

export default function PageFullCase() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [caseData, setCaseData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/cases/${id}`)
      .then(res => {
        if (!res.ok) throw new Error('Ошибка загрузки кейса');
        return res.json();
      })
      .then(data => {
        setCaseData(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [id]);

  const acceptCase = () => {
    fetch(`http://localhost:3001/cases/${id}/status`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ status: 'accepted' }),
    })
      .then(res => {
        if (!res.ok) throw new Error('Ошибка обновления статуса');
        return res.json();
      })
      .then(() => {
        alert('Кейс принят');
        // Обновить состояние кейса чтобы отобразить новый статус
        setCaseData({ ...caseData, status: 'accepted' });
      })
      .catch(err => alert(err.message));
  };

  if (loading) return <p>Загрузка кейса...</p>;
  if (error) return <p>Ошибка: {error}</p>;
  if (!caseData) return <p>Кейс не найден</p>;

  return (
    <div className={styles.container}>
      <h1 className={styles.title}>{caseData.title}</h1>
      {caseData.cover && <img src={`http://localhost:3001${caseData.cover}`} alt="Обложка" className={styles.cover} />}
      <p><b>Заказчик:</b> {caseData.userEmail}</p>
      <p><b>Тема:</b> {caseData.theme}</p>
      <p><b>Задача проекта:</b> {caseData.description}</p>
      <div className={styles.filesSection}>
        <b>Прикрепленные файлы:</b>
        <div className={styles.filesList}>
          {caseData.files && caseData.files.map((file, i) => (
            <a key={i} href={`http://localhost:3001${file}`} target="_blank" rel="noreferrer" className={styles.fileItem}>
              {file.split('/').pop()}
            </a>
          ))}
        </div>
      </div>
      {caseData.status !== 'accepted' && (
        <button className={styles.acceptButton} onClick={acceptCase}>
          Принять кейс
        </button>
      )}
      <p>Статус: {caseData.status}</p>
    </div>
  );
}
