import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styles from './AddCasePage.module.css'; // стили по желанию

export default function AddCasePage() {
  const [projectName, setProjectName] = useState('');
  const [theme, setTheme] = useState('');
  const [description, setDescription] = useState('');
  const [files, setFiles] = useState([]);
  const [cover, setCover] = useState(null);

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files));
  };

  const handleCoverChange = (e) => {
    setCover(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // здесь можно добавить логику отправки данных на сервер
    console.log('Проект создан', { projectName, theme, description, files, cover });
  };

  return (

<>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

    <div className={styles.container}>
      <h2>Описание проекта</h2>
      <form onSubmit={handleSubmit} className={styles.form}>
        <label>
          Укажите название проекта<br></br>
          <input 
            type="text"
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
            className={styles.input}
          />
        </label>

        <label>
          Выберите тему
          <input 
            type="text"
            value={theme}
            onChange={(e) => setTheme(e.target.value)}
            className={styles.input}
          />
        </label>

        <label>
          Опишите детально задачу и суть проекта
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            className={styles.textarea}
          ></textarea>
        </label>

        <label>
          Прикрепить файл
          <input type="file" multiple onChange={handleFileChange} />
        </label>

        <label>
          Выбрать обложку
          <input type="file" onChange={handleCoverChange} />
        </label>

        <button type="submit" className={styles.submitButton}>Разместить проект</button>
      </form>
    </div>

    <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            Связаться с нами <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a> <br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer"><img src="images/facebook.svg" alt="Facebook" /></a>
            <a href="https://twitter.com" target="_blank" rel="noopener noreferrer"><img src="images/twitterx.svg" alt="Twitter" /></a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer"><img src="images/instagram.svg" alt="Instagram" /></a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
