import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import styles from './AddCasePage.module.css';

export default function AddCasePage() {
  const [projectName, setProjectName] = useState('');
  const [theme, setTheme] = useState('');
  const [description, setDescription] = useState('');
  const [files, setFiles] = useState([]);
  const [cover, setCover] = useState(null);

  const navigate = useNavigate();

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files));
  };

  const handleCoverChange = (e) => {
    setCover(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const userId = localStorage.getItem('userId');
    if (!userId) {
      alert('Вы не авторизованы');
      navigate('/signin');
      return;
    }

    // Используем FormData для отправки файлов и полей
    const formData = new FormData();
    formData.append('userId', userId);
    formData.append('title', projectName);
    formData.append('theme', theme);
    formData.append('description', description);
    if (cover) {
      formData.append('cover', cover);
    }
    files.forEach((file, idx) => formData.append(`files`, file));

    try {
      const response = await fetch('http://localhost:3001/cases', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) {
        const err = await response.json();
        throw new Error(err.error || 'Ошибка создания кейса');
      }
      alert('Кейс успешно создан!');
      navigate('/cases');
    } catch (error) {
      alert('Ошибка: ' + error.message);
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
        </nav>
      </header>
      <div className={styles.container}>
        <h2>Описание проекта</h2>
        <form onSubmit={handleSubmit} className={styles.form}>
          <label>
            Укажите название проекта
            <input
              type="text"
              value={projectName}
              onChange={(e) => setProjectName(e.target.value)}
              required
              className={styles.input}
            />
          </label>
          <label>
            Выберите тему
            <input
              type="text"
              value={theme}
              onChange={(e) => setTheme(e.target.value)}
              required
              className={styles.input}
            />
          </label>
          <label>
            Опишите детально задачу и суть проекта
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              className={styles.textarea}
            />
          </label>
          <label>
            Прикрепить файлы (до 15)
            <input type="file" multiple onChange={handleFileChange} />
          </label>
          <label>
            Выбрать обложку
            <input type="file" onChange={handleCoverChange} />
          </label>
          <button type="submit" className={styles.submitButton}>Разместить проект</button>
        </form>
      </div>
      <footer className={styles.footer}>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
