import React, { useEffect, useState } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import styles from './PageFullProcessedCase.module.css';

export default function PageFullProcessedCase() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [caseData, setCaseData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const userId = localStorage.getItem('currentUserId');

  useEffect(() => {
    setLoading(true);
    setError(null);
    
    // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –∏–∑ processed-cases –≤–º–µ—Å—Ç–æ cases
    fetch(`http://localhost:3001/processed-cases/${id}`)
      .then(res => {
        if (!res.ok) {
          throw new Error(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –∫–µ–π—Å–∞: ${res.status} ${res.statusText}`);
        }
        return res.json();
      })
      .then(data => {
        setCaseData(data);
      })
      .catch(err => {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –∫–µ–π—Å–∞:', err);
        setError(err.message);
      })
      .finally(() => {
        setLoading(false);
      });
  }, [id]);

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ñ–∞–π–ª–æ–≤ –∫ —Ç–µ–∫—É—â–µ–º—É –ø—Ä–æ–µ–∫—Ç—É
  const handleAddFiles = (e) => {
    const files = e.target.files;
    if (!files || files.length === 0) return;

    const formData = new FormData();
    Array.from(files).forEach((file) => {
      formData.append('extraFiles', file);
    });

    fetch(`http://localhost:3001/processed-cases/${id}/upload-files`, {
      method: 'POST',
      body: formData,
    })
    .then(async res => {
      if (!res.ok) {
        const errorResult = await res.json();
        throw new Error(errorResult.error || '–û—à–∏–±–∫–∞ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ñ–∞–π–ª–æ–≤');
      }
      return res.json();
    })
    .then(updatedCase => {
      alert('–§–∞–π–ª—ã —É—Å–ø–µ—à–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω—ã');
      setCaseData(updatedCase);
    })
    .catch(err => {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –¥–æ–±–∞–≤–ª–µ–Ω–∏–∏ —Ñ–∞–π–ª–æ–≤:', err);
      alert('–û—à–∏–±–∫–∞: ' + err.message);
    });
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∫–µ–π—Å–∞
  const handleCompleteCase = () => {
    if (!userId) {
      alert('–í—ã –¥–æ–ª–∂–Ω—ã –≤–æ–π—Ç–∏ –≤ —Å–∏—Å—Ç–µ–º—É');
      navigate('/signin');
      return;
    }

    fetch(`http://localhost:3001/processed-cases/${id}/complete`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userId: parseInt(userId, 10) }),
    })
    .then(async res => {
      if (!res.ok) {
        const text = await res.text();
        throw new Error(text || '–û—à–∏–±–∫–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∫–µ–π—Å–∞');
      }
      return res.json();
    })
    .then(data => {
      alert('–ö–µ–π—Å –∑–∞–≤–µ—Ä—à—ë–Ω –∏ –¥–æ–±–∞–≤–ª–µ–Ω –≤ –ø—Ä–æ–µ–∫—Ç—ã');
      // –û–±–Ω–æ–≤–ª—è–µ–º –¥–∞–Ω–Ω—ã–µ –∫–µ–π—Å–∞
      setCaseData(prev => ({
        ...prev,
        status: 'closed'
      }));
    })
    .catch(err => {
      console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≤–µ—Ä—à–µ–Ω–∏–∏ –∫–µ–π—Å–∞:', err);
      alert('–û—à–∏–±–∫–∞ –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –∫–µ–π—Å–∞: ' + err.message);
    });
  };

  if (loading) return <p>–ó–∞–≥—Ä—É–∑–∫–∞ –ø—Ä–æ–µ–∫—Ç–∞...</p>;
  if (error) return <p>–û—à–∏–±–∫–∞: {error}</p>;
  if (!caseData) return <p>–ü—Ä–æ–µ–∫—Ç –Ω–µ –Ω–∞–π–¥–µ–Ω</p>;

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="/images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">–ü—Ä–æ—Ñ–∏–ª—å</Link>
          <Link to="/cases">–ö–µ–π—Å—ã</Link>
          <Link to="/projects">–ü—Ä–æ–µ–∫—Ç—ã</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>–†–∞–∑–º–µ—Å—Ç–∏—Ç—å –ø—Ä–æ–µ–∫—Ç</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>–ü—Ä–∏—Å—Ç—É–ø–∏—Ç—å –∫ –ø—Ä–æ–µ–∫—Ç—É</button>
          </Link>
        </nav>
      </header>

      <main className={styles.container}>
        <h1 className={styles.title}>{caseData.title}</h1>
        
        {caseData.cover && (
          <img 
            src={`http://localhost:3001${caseData.cover}`} 
            alt="–û–±–ª–æ–∂–∫–∞" 
            className={styles.cover} 
          />
        )}
        
        <div className={styles.infoGrid}>
          <div className={styles.infoItem}>
            <b>–ó–∞–∫–∞–∑—á–∏–∫:</b>
            {caseData.userId ? (
              <Link to={`/profileview/${caseData.userId}`}>
                {caseData.userEmail}
              </Link>
            ) : (
              caseData.userEmail
            )}
          </div>

          <div className={styles.infoItem}>
            <b>–ò—Å–ø–æ–ª–Ω–∏—Ç–µ–ª—å:</b> 
            {caseData.executorId ? (
              <Link to={`/profileview/${caseData.executorId}`}>
                {caseData.executorEmail || '–í—ã'}
              </Link>
            ) : (
              caseData.executorEmail || '–ù–µ –Ω–∞–∑–Ω–∞—á–µ–Ω'
            )}
          </div>

          <div className={styles.infoItem}>
            <b>–¢–µ–º–∞:</b> {caseData.theme}
          </div>

          <div className={styles.infoItem}>
            <b>–°—Ç–∞—Ç—É—Å:</b> 
            <span className={`${styles.status} ${styles[caseData.status]}`}>
              {caseData.status === 'in_process' ? '–í –ø—Ä–æ—Ü–µ—Å—Å–µ' : 
               caseData.status === 'closed' ? '–ó–∞–≤–µ—Ä—à–µ–Ω' : caseData.status}
            </span>
          </div>
        </div>

        <div className={styles.description}>
          <h3>–ó–∞–¥–∞—á–∞ –ø—Ä–æ–µ–∫—Ç–∞:</h3>
          <p>{caseData.description}</p>
        </div>

        <div className={styles.filesSection}>
          <h3>–ü—Ä–∏–∫—Ä–µ–ø–ª–µ–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã:</h3>
          <div className={styles.filesList}>
            {caseData.files && caseData.files.length > 0 ? (
              caseData.files.map((file, i) => (
                <a 
                  key={i} 
                  href={`http://localhost:3001${file}`} 
                  target="_blank" 
                  rel="noreferrer" 
                  className={styles.fileItem}
                >
                  üìé {file.split('/').pop()}
                </a>
              ))
            ) : (
              <p>–§–∞–π–ª—ã –æ—Ç—Å—É—Ç—Å—Ç–≤—É—é—Ç</p>
            )}
          </div>

          {/* –ë–ª–æ–∫ –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ñ–∞–π–ª–æ–≤ (—Ç–æ–ª—å–∫–æ –¥–ª—è –∏—Å–ø–æ–ª–Ω–∏—Ç–µ–ª—è) */}
          {caseData.executorId === parseInt(userId, 10) && caseData.status === 'in_process' && (
            <div className={styles.fileUploadSection}>
              <label className={styles.fileUploadLabel}>
                üì§ –î–æ–±–∞–≤–∏—Ç—å —Ñ–∞–π–ª—ã –∫ –ø—Ä–æ–µ–∫—Ç—É
                <input
                  type="file"
                  multiple
                  onChange={handleAddFiles}
                  className={styles.hiddenFileInput}
                />
              </label>
            </div>
          )}
        </div>

        {/* –ö–Ω–æ–ø–∫–∏ –¥–µ–π—Å—Ç–≤–∏–π */}
        <div className={styles.actionButtons}>
          {caseData.status === 'in_process' && caseData.executorId === parseInt(userId, 10) && (
            <button 
              className={styles.completeButton} 
              onClick={handleCompleteCase}
            >
              ‚úÖ –ó–∞–≤–µ—Ä—à–∏—Ç—å –ø—Ä–æ–µ–∫—Ç
            </button>
          )}
          
          <button 
            className={styles.backButton} 
            onClick={() => navigate(-1)}
          >
            ‚Üê –ù–∞–∑–∞–¥
          </button>
        </div>
      </main>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img src="/images/logobig.svg" alt="Big Logo" />
          </div>
          <div className={styles.footerContacts}>
            –°–≤—è–∑–∞—Ç—å—Å—è —Å –Ω–∞–º–∏ <br />
            <a href="mailto:support@ideaflow.com">support@ideaflow.com</a><br />
            <p>+7 (123) 456-78-90</p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#"><img src="/images/facebook.svg" alt="Facebook" /></a>
            <a href="#"><img src="/images/twitterx.svg" alt="Twitter" /></a>
            <a href="#"><img src="/images/instagram.svg" alt="Instagram" /></a>
          </div>
        </div>
        <p style={{ fontSize: 20, textAlign: 'center', marginTop: 10 }}>
          –ú–µ—Å—Ç–æ, –≥–¥–µ –∏–¥–µ–∏ –ø—Ä–µ–≤—Ä–∞—â–∞—é—Ç—Å—è –≤ —É—Å–ø–µ—à–Ω—ã–µ –ø—Ä–æ–µ–∫—Ç—ã –±–ª–∞–≥–æ–¥–∞—Ä—è —Å–æ—Ç—Ä—É–¥–Ω–∏—á–µ—Å—Ç–≤—É –∑–∞–∫–∞–∑—á–∏–∫–æ–≤ –∏ —Ñ—Ä–∏–ª–∞–Ω—Å–µ—Ä–æ–≤.
        </p>
      </footer>
    </>
  );
}
