import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import styles from './HomePage.module.css';

export default function HomePage() {
  const [activeTab, setActiveTab] = useState('done');
  const [doneProjects, setDoneProjects] = useState([]);
  const [openProjects, setOpenProjects] = useState([]);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);
  const [isTablet, setIsTablet] = useState(window.innerWidth <= 1024 && window.innerWidth > 768);
  const navigate = useNavigate();
  
  const userId = localStorage.getItem('currentUserId');

  useEffect(() => {
    // Определение типа устройства
    const handleResize = () => {
      const width = window.innerWidth;
      setIsMobile(width <= 768);
      setIsTablet(width <= 1024 && width > 768);
    };

    window.addEventListener('resize', handleResize);
    
    // Загрузить завершенные проекты
    fetch('http://localhost:3001/projects')
      .then(res => res.json())
      .then(data => {
        const closedProjects = data.filter(p => p.status === 'closed');
        setDoneProjects(closedProjects.slice(-3));
      })
      .catch(() => setDoneProjects([]));

    // Загрузить открытые кейсы
    fetch('http://localhost:3001/cases')
      .then(res => res.json())
      .then(data => {
        const openCases = data.filter(c => c.status === 'open');
        setOpenProjects(openCases.slice(-3));
      })
      .catch(() => setOpenProjects([]));

    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const projectList = activeTab === 'done' ? doneProjects : openProjects;

  const handleShowMoreClick = () => {
    if (activeTab === 'done') {
      navigate('/projects');
      window.scrollTo({ top: 0, behavior: 'smooth' });
    } else if (activeTab === 'open') {
      navigate('/cases');
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Функция для обрезки длинного email
  const truncateEmail = (email) => {
    if (isMobile && email && email.length > 15) {
      return email.substring(0, 12) + '...';
    }
    return email;
  };

  // Функция для обрезки длинного заголовка
  const truncateTitle = (title) => {
    if (!title) return '';
    if (isMobile && title.length > 20) {
      return title.substring(0, 18) + '...';
    }
    if (isTablet && title.length > 25) {
      return title.substring(0, 23) + '...';
    }
    return title;
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img 
            src="/images/logosmall.svg" 
            alt="IdeaFlow logo" 
            style={{ height: isMobile ? 50 : 80 }} 
          />
        </Link>
        <nav className={styles.navLinks}>
          <Link to={userId ? "/profile" : "/signin"}>Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <div className={styles.desktopButtons}>
            <Link to={userId ? "/profile" : "/signin"}>
              <button className={styles.buttonYellow}>Разместить проект</button>
            </Link>
            <Link to="/cases">
              <button className={styles.buttonYellow}>Приступить к проекту</button>
            </Link>
          </div>
        </nav>
        
        {/* Мобильные кнопки в ряд по 2 */}
        <div className={styles.mobileButtons}>
          <Link to={userId ? "/profile" : "/signin"}>
            <button className={styles.buttonYellow}>
              Разместить проект
            </button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>
              Приступить к проекту
            </button>
          </Link>
        </div>
      </header>

      <section className={styles.sectionIntro}>
        <img src="/images/hand.svg" alt="Lightbulb in hand" />
        <div className={styles.textContainer}>
          <h1>IdeaFlow</h1>
          <p>— где идеи встречают исполнение</p>
        </div>
      </section>

      <main className={styles.mainContent}>
        <div
          className={styles.projectsContainer}
          style={{
            width: '100%',
            transition: 'background-color 0.3s ease',
          }}
        >
          <div className={styles.tabs} style={{ width: '100%' }}>
            <button
              className={`${styles.tabButton} ${styles.left}`}
              onClick={() => setActiveTab('done')}
              style={{
                backgroundColor: activeTab === 'done' ? '#F5F5F5' : '#0E900E',
                color: activeTab === 'done' ? '#0E900E' : '#F5F5F5',
                borderRadius: isMobile ? '12px 12px 0 0' : '20px 20px 0 0',
              }}
            >
              Реализованные идеи
            </button>
            <button
              className={`${styles.tabButton} ${styles.right}`}
              onClick={() => setActiveTab('open')}
              style={{
                backgroundColor: activeTab === 'open' ? '#F5F5F5' : '#0E900E',
                color: activeTab === 'open' ? '#0E900E' : '#F5F5F5',
                borderRadius: isMobile ? '12px 12px 0 0' : '20px 20px 0 0',
                borderLeft: 'none',
              }}
            >
              Открытые проекты
            </button>
          </div>

          <p style={{ 
            color: '#0E900E', 
            textAlign: 'center', 
            margin: isMobile ? 15 : 20,
            fontSize: isMobile ? '14px' : 'inherit',
            fontWeight: 'bold'
          }}>
            Рекомендации для вас
          </p>

          <div className={styles.projectsGrid}>
            {projectList.map(({ id, userEmail, performerEmail, theme, cover, title }) => (
              activeTab === 'done' ? (
                <Link key={id} to={`/projects/${id}`} className={styles.projectCardLink}>
                  <div className={styles.projectCard}>
                    {cover && (
                      <img 
                        src={`http://localhost:3001${cover}`} 
                        alt={`Фото исполнителя ${performerEmail}`} 
                      />
                    )}
                    <div style={{ padding: isMobile ? '10px' : '8px' }}>
                      <span style={{ 
                        fontSize: isMobile ? '13px' : isTablet ? '14px' : '16px',
                        display: 'block',
                        marginBottom: '4px'
                      }}>
                        {truncateEmail(performerEmail)}
                      </span>
                      <span style={{ 
                        fontSize: isMobile ? '12px' : isTablet ? '13px' : '14px',
                        color: '#0E900E',
                        fontWeight: 'bold'
                      }}>
                        {truncateTitle(theme || title)}
                      </span>
                    </div>
                  </div>
                </Link>
              ) : (
                <Link key={id} to={`/cases/${id}`} className={styles.projectCardLink}>
                  <div className={styles.projectCard}>
                    {cover && (
                      <img 
                        src={`http://localhost:3001${cover}`} 
                        alt={`Фото заказчика ${userEmail}`} 
                      />
                    )}
                    <div style={{ padding: isMobile ? '10px' : '8px' }}>
                      <span style={{ 
                        fontSize: isMobile ? '13px' : isTablet ? '14px' : '16px',
                        display: 'block',
                        marginBottom: '4px'
                      }}>
                        {truncateEmail(userEmail)}
                      </span>
                      <span style={{ 
                        fontSize: isMobile ? '12px' : isTablet ? '13px' : '14px',
                        color: '#0E900E',
                        fontWeight: 'bold'
                      }}>
                        {truncateTitle(theme || title)}
                      </span>
                    </div>
                  </div>
                </Link>
              )
            ))}
          </div>

          <button
            className={styles.showMoreButton}
            style={{ 
              color: '#0E900E',
              fontSize: isMobile ? '16px' : '20px',
              fontWeight: 'bold'
            }}
            onClick={handleShowMoreClick}
          >
            Показать еще
          </button>
        </div>

        <section className={styles.sectionHelp}>
          <p>
            IdeaFlow помогает быстро находить исполнителей для любых творческих и технических проектов, облегчая процесс поиска и сотрудничества.
          </p>
          <img src="/images/womenwhithlamp.svg" alt="Woman holding lightbulbs" />
        </section>

        <section className={styles.sectionPartnership}>
          <p>
            Здесь встречаются идеи и профессиональное выполнение, создавая пространство для эффективной работы и взаимовыгодного партнерства.
          </p>
        </section>
      </main>

      <footer className={styles.footer}>
        <div className={styles.footerContainer}>
          <div className={styles.footerLogo}>
            <img 
              src="/images/logobig.svg" 
              alt="Big Logo" 
              style={{ width: isMobile ? '100px' : 'auto' }} 
            />
          </div>
          <div className={styles.footerContacts}>
            <span style={{ fontSize: isMobile ? '14px' : '20px' }}>
              Связаться с нами
            </span>
            <br />
            <a href="mailto:support@ideaflow.com" style={{ fontSize: isMobile ? '13px' : 'inherit' }}>
              support@ideaflow.com
            </a>
            <br />
            <p style={{ fontSize: isMobile ? '13px' : 'inherit', margin: '5px 0 0 0' }}>
              +7 (123) 456-78-90
            </p>
          </div>
          <div className={styles.footerSocials}>
            <a href="#">
              <img src="/images/facebook.svg" alt="Facebook" />
            </a>
            <a href="#">
              <img src="/images/twitterx.svg" alt="Twitter" />
            </a>
            <a href="#">
              <img src="/images/instagram.svg" alt="Instagram" />
            </a>
          </div>
        </div>
        <p style={{ 
          fontSize: isMobile ? 14 : 20, 
          textAlign: 'center', 
          marginTop: isMobile ? 12 : 10,
          lineHeight: '1.4'
        }}>
          Место, где идеи превращаются в успешные проекты благодаря сотрудничеству заказчиков и фрилансеров.
        </p>
      </footer>
    </>
  );
}
