import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { auth } from '../firebase'; 
import { createUserWithEmailAndPassword } from "firebase/auth";
import styles from './RegisterPage.module.css';

export default function RegisterPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      navigate('/profile');  // после регистрации переходим на профиль
    } catch (err) {
      if (err.code === 'auth/email-already-in-use') setError('Этот email уже зарегистрирован.');
      else if (err.code === 'auth/invalid-email') setError('Неверный формат email.');
      else if (err.code === 'auth/weak-password') setError('Пароль слишком слабый.');
      else setError('Ошибка: ' + err.message);
    }
  };

  return (
    <>
      <header className={styles.header}>
        <Link to="/">
          <img src="images/logosmall.svg" alt="IdeaFlow logo" style={{ height: 80 }} />
        </Link>
        <nav className={styles.navLinks}>
          <Link to="/profile">Профиль</Link>
          <Link to="/cases">Кейсы</Link>
          <Link to="/projects">Проекты</Link>
          <Link to="/profile">
            <button className={styles.buttonYellow}>Разместить проект</button>
          </Link>
          <Link to="/cases">
            <button className={styles.buttonYellow}>Приступить к проекту</button>
          </Link>
        </nav>
      </header>

      <form onSubmit={handleRegister} className={styles.form}>
        <h2>Регистрация</h2>
        <input 
          type="email" placeholder="Email" 
          value={email} onChange={e => setEmail(e.target.value)}
          required 
        />
        <input 
          type="password" placeholder="Пароль" 
          value={password} onChange={e => setPassword(e.target.value)}
          required 
        />
        <button type="submit">Зарегистрироваться</button>
        {error && <p style={{color: 'red'}}>{error}</p>}
        <span className={styles.switchLink} onClick={() => navigate('/signin')}>
          Уже есть аккаунт? Войти
        </span>
      </form>
    </>
  );
}
